import React, { Component } from 'react';
import './App.css';
import {Helmet} from "react-helmet";
import Button from 'antd/lib/button';
import { Row, Col } from 'antd';
import { Responsive as ResponsiveGridLayout } from 'react-grid-layout';


const API_KEY = 'AIzaSyAJNRET1i72IoEvCwsonTd6TevKHelOqBU';
const fontDict = {};

class App extends Component {
  state = {
    flipped: false,
    dict:fontDict,
    sansSerifNumber: 0
  }

  componentDidMount(){
    fetch(`https://www.googleapis.com/webfonts/v1/webfonts?key=${API_KEY}`)
    .then(res => res.json())
    .then(data => {
      let sansSerif = []
      let serif = []
      let monospace = []
      for(let i = 0 ; i< data.items.length; i++){
        if (data.items[i].category === "sans-serif"){
          sansSerif.push(data.items[i].family);
        }
        else if (data.items[i].category === "serif"){
          serif.push(data.items[i].family);
        }
        else if (data.items[i].category === "monospace"){
          monospace.push(data.items[i].family);
        }
      }
      fontDict['sans-serif'] = sansSerif;
      fontDict['serif'] = serif;
      fontDict['monospace'] = monospace;
      //console.log(fontDict);
    })
  }

  toggle = async(e) => {
    e.preventDefault();
    // Random pick font
    let min = 0;
    let sansSerifMax = this.state.dict['sans-serif'].length
    let chooseFont = this.state.dict['sans-serif'][Math.round(Math.random() * sansSerifMax - 1, 0)]

    // Random pick color
    var colorLetters = '0123456789ABCDEF';
    var colory = '#';
    for (var i = 0; i < 6; i++ ) {
      colory += colorLetters[Math.floor(Math.random() * 16)];
    }

    // Random pick XY
    let direction = Math.floor(Math.random() * Math.floor(2))
    var chooseBackOp = ''
    var chooseFlipOp = ''
    console.log(direction);
    if (direction === 0){
      chooseBackOp = 'rotateX(180deg)';
      chooseFlipOp = 'rotateX(540deg)';
    }else{
      chooseBackOp = 'rotateY(180deg)';
      chooseFlipOp = 'rotateY(540deg)';
    }

    this.setState({
      flipped: !this.state.flipped,
      sansSerifNumber: Math.round(Math.random() * sansSerifMax , 0),
      sansSerifFont: chooseFont,
      sansSerifFontUrl: "https://fonts.googleapis.com/css?family=" + chooseFont,
      color: colory,
      chooseBack: chooseBackOp,
      chooseFlip: chooseFlipOp
    });
  }



  letter(l){
    const titleString = l
    const titleList = titleString.split('')
    let cardClassX = ["card"];
    let cardClassY = ["card2"];
    if(this.state.flipped) {
      cardClassX.push('flippedX');
    }
    if(this.state.flipped) {
      cardClassY.push('flippedY');
    }

    return(titleList.map((title) =>
    <div>
      <div className={cardClassX.join(' ')}>
        <div className="front">{title}</div>
        <div className="backX">{title}</div>
      </div>
    </div>
  ))
}

letter2(l){
  const titleString = l
  const titleList = titleString.split('')
  let cardClassX = ["card"];
  let cardClassY = ["card2"];
  if(this.state.flipped) {
    cardClassX.push('flippedX');
  }
  if(this.state.flipped) {
    cardClassY.push('flippedY');
  }

  return(titleList.map((title) =>
  <div>
    <div className={cardClassY.join(' ')}>
      <div className="front">{title}</div>
      <div className="backY">{title}</div>
    </div>
  </div>
))
}

render() {
  var layout = [
        {i: 'a', x: 0, y: 0, w: 1, h: 2, static: true},
        {i: 'b', x: 1, y: 0, w: 3, h: 2, minW: 2, maxW: 4},
        {i: 'c', x: 4, y: 0, w: 1, h: 2}
      ];

  return(
    <div >
      <Helmet>
        <link href={this.state.sansSerifFontUrl} rel="stylesheet"></link>
      </Helmet>
      <div className="body-container">
      <div className="container" style={{backgroundColor:this.state.color,fontFamily:this.state.sansSerifFont}}>
        {this.letter2('PING')}
      </div>
      {this.state.sansSerifFont}
      <div style={{backgroundColor:this.state.color}} className='box' onClick={this.toggle.bind(this)}>Click Me</div>
    </div>
    <div>

      <Button>Button</Button>

        <ResponsiveGridLayout className="layout" layouts={layouts}
        breakpoints={{lg: 1200, md: 996, sm: 768, xs: 480, xxs: 0}}
        cols={{lg: 12, md: 10, sm: 6, xs: 4, xxs: 2}}>
        <div key="a">1</div>
        <div key="b">2</div>
        <div key="c">3</div>
      </ResponsiveGridLayout>
    </div>
</div>
  );
}
}

export default App;
