import React, { Component } from 'react';
import './App.css';

const API_KEY = 'AIzaSyAJNRET1i72IoEvCwsonTd6TevKHelOqBU';

class App extends Component {
  state = {flipped: false}

  toggle = async(e) => {
    e.preventDefault();
    this.setState({flipped: !this.state.flipped});
    const api_call = await fetch(`https://www.googleapis.com/webfonts/v1/webfonts?key=${API_KEY}`)
    const data = await api_call.json();
    console.log(data);

    var apiUrl = [];
    apiUrl.push('https://fonts.googleapis.com/css?family=');
    apiUrl.push(anonymousPro.family.replace(/ /g, '+'));
    if (contains('italic', anonymousPro.variants)) {
      apiUrl.push(':');
      apiUrl.push('italic');
    }
    if (contains('greek', anonymousPro.subsets)) {
      apiUrl.push('&subset=');
      apiUrl.push('greek');
    }

    // url: 'https://fonts.googleapis.com/css?family=Anonymous+Pro:italic&subset=greek'
    var url = apiUrl.join('');
    console.log(url);
  }

  letter(){
    const titleList = ['P','I','N','G']
    let cardClass = ["card"];
    if(this.state.flipped) {
      cardClass.push('flipped');
    }

    return(titleList.map((title) =>
    <div className={cardClass.join(' ')}>
      <div className="front">{title}</div>
      <div className="back">{title}</div>
    </div>

  ))
  }

  render() {
    let boxClass = ["box"];
    if(this.state.flipped) {
      boxClass.push('green');
    }

    return(
      <div>
        <div className="container">
          {this.letter()}
        </div>

        <div className={boxClass.join(' ')} onClick={this.toggle.bind(this)}>Click Me</div>

      </div>

    );
  }
}

export default App;
