import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {flipped: false}

  toggle() {
    this.setState({flipped: !this.state.flipped});
  }

  render() {
    let boxClass = ["box"];
    if(this.state.flipped) {
      boxClass.push('green');
    }
    let cardClass = ["card"];
    if(this.state.flipped) {
      cardClass.push('flipped');
    }
    return(
      <div>
        <div className={boxClass.join(' ')} onClick={this.toggle.bind(this)}></div>

        <div class="container">
          <div class={cardClass.join(' ')}>
            <div class="front">C</div>
            <div class="back">Y</div>
          </div>
        </div>
      </div>

    );
  }
}

export default App;
