import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {flipped: false}

  toggle() {
    this.setState({flipped: true})
  }

  render() {
    let cardClass = ["box"];
    if(this.state.flipped) {
      cardClass.push('green');
    }
    return(
        <div className={cardClass.join(' ')} onClick={this.toggle()}></div>
    );
  }
}

export default App;
