import React, { Component } from 'react';
import './App.css';
import {Helmet} from "react-helmet";


const API_KEY = 'AIzaSyAJNRET1i72IoEvCwsonTd6TevKHelOqBU';
const dict = {};

class App extends Component {
  state = {flipped: false}

  componentDidMount(){
    fetch(`https://www.googleapis.com/webfonts/v1/webfonts?key=${API_KEY}`)
    .then(res => res.json())
    .then(data => {
      let sansSerif = []
      for(let i = 0 ; i< data.items.length; i++){
        if (data.items[i].category === "sans-serif"){
          sansSerif.push(data.items[i].family);
        }
      }
      dict['sans-serif'] = sansSerif;
      console.log(dict);
    })
  }

  toggle = async(e) => {
    e.preventDefault();
    this.setState({flipped: !this.state.flipped});

  }



  letter(){
    const titleList = ['P','I','N','G']
    let cardClass = ["card"];
    if(this.state.flipped) {
      cardClass.push('flipped');
    }

    return(titleList.map((title) =>
    <div className={cardClass.join(' ')}>
      <div className="front">{title}</div>
      <div className="back">{title}</div>
    </div>

  ))
}

render() {
  let boxClass = ["box"];
  if(this.state.flipped) {
    boxClass.push('green');
  }
  const divStyle = {
    fontFamily: 'Oxygen',
    border: '5px solid pink'
  };



  return(
    <div >
      <Helmet>
        <link href="https://fonts.googleapis.com/css?family=Oxygen" rel="stylesheet"></link>
      </Helmet>
      <div className="container">
        {this.letter()}
      </div>

      <div style={divStyle} className={boxClass.join(' ')} onClick={this.toggle.bind(this)}>Click Me</div>
    </div>

  );
}
}

export default App;
