import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {flipped: false}

  toggle() {
    this.setState({flipped: !this.state.flipped});
  }

  letter(){
    const titleList = ['P','I','N','G']
    let cardClass = ["card"];
    if(this.state.flipped) {
      cardClass.push('flipped');
    }

    return(titleList.map((title) =>
    <div class={cardClass.join(' ')}>
      <div class="front">{title}</div>
      <div class="back">{title}</div>
    </div>

  ))
  }

  render() {
    let boxClass = ["box"];
    if(this.state.flipped) {
      boxClass.push('green');
    }

    return(
      <div>

        <div class="container">
          {this.letter()}
        </div>

        <div className={boxClass.join(' ')} onClick={this.toggle.bind(this)}></div>

      </div>

    );
  }
}

export default App;
