import React, { Component } from 'react';
import './App.css';
import {Helmet} from "react-helmet";
import { Row, Col } from 'antd';


const API_KEY = 'AIzaSyAJNRET1i72IoEvCwsonTd6TevKHelOqBU';
const fontDict = {};

class App extends Component {
  state = {
    flipped: false,
    dict:fontDict,
    sansSerifNumber: 0
  }

  componentDidMount(){
    fetch(`https://www.googleapis.com/webfonts/v1/webfonts?key=${API_KEY}`)
    .then(res => res.json())
    .then(data => {
      let sansSerif = []
      let serif = []
      let monospace = []
      for(let i = 0 ; i< data.items.length; i++){
        if (data.items[i].category === "sans-serif"){
          sansSerif.push(data.items[i].family);
        }
        else if (data.items[i].category === "serif"){
          serif.push(data.items[i].family);
        }
        else if (data.items[i].category === "monospace"){
          monospace.push(data.items[i].family);
        }
      }
      fontDict['sans-serif'] = sansSerif;
      fontDict['serif'] = serif;
      fontDict['monospace'] = monospace;
      //console.log(fontDict);
    })
  }

  toggle = async(e) => {
    e.preventDefault();
    // Random pick font
    let min = 0;
    let sansSerifMax = this.state.dict['sans-serif'].length
    let chooseFont = this.state.dict['sans-serif'][Math.round(Math.random() * sansSerifMax - 1, 0)]

    // Random pick color
    var colorLetters = '0123456789ABCDEF';
    var colory = '#';
    for (var i = 0; i < 6; i++ ) {
      colory += colorLetters[Math.floor(Math.random() * 16)];
    }

    // Random pick XY
    let direction = Math.floor(Math.random() * Math.floor(2))
    var chooseBackOp = ''
    var chooseFlipOp = ''
    console.log(direction);
    if (direction === 0){
      chooseBackOp = 'rotateX(180deg)';
      chooseFlipOp = 'rotateX(540deg)';
    }else{
      chooseBackOp = 'rotateY(180deg)';
      chooseFlipOp = 'rotateY(540deg)';
    }

    this.setState({
      flipped: !this.state.flipped,
      sansSerifNumber: Math.round(Math.random() * sansSerifMax , 0),
      sansSerifFont: chooseFont,
      sansSerifFontUrl: "https://fonts.googleapis.com/css?family=" + chooseFont,
      color: colory,
      chooseBack: chooseBackOp,
      chooseFlip: chooseFlipOp
    });
  }



  letter(l){
    const titleString = l
    const titleList = titleString.split('')
    let cardClass = ["card"];
    if(this.state.flipped) {
      cardClass.push('flipped');
    }

    return(titleList.map((title) =>
    <div>
      <div className={cardClass.join(' ')}>
        <div className="front">{title}</div>
        <div className="back">{title}</div>
      </div>
    </div>
  ))
}

flippedCard(l){
  const titleString = l
  const titleList = titleString.split('')
  let cardClass = ["card"];
  if(this.state.flipped) {
    cardClass.push('flippedY');
  }
  return(
  <div>
    <div className={cardClass.join(' ')} >
      <div className="front">{titleString}</div>
      <div className="backY">{titleString}</div>
    </div>
  </div>
)
}

render() {

  return(
    <div >
      <Helmet>
        <link href={this.state.sansSerifFontUrl} rel="stylesheet"></link>
      </Helmet>
      <div className="body-container">
        <div className="container" style={{backgroundColor:this.state.color,fontFamily:this.state.sansSerifFont}}>
          {this.letter('PING')}
        </div>
        <div className="container" style={{backgroundColor:this.state.color,fontFamily:this.state.sansSerifFont}}>
          {this.letter('PING')}
        </div>
        <button className='change-btn' style={{backgroundColor:this.state.color}} onClick={this.toggle.bind(this)}> Yes, Here </button>
        <Row>
          <Col xs={6} md={12} lg={14}>
            <div className='edu-container'>
              <div>
                <p className='sub-title'>Education</p>
                <p className='sub-desc'>Feb 2017 - Dec 2018:</p>
                <p className='sub-desc'>Master of Information Technology</p>
                <p className='sub-desc'>	- Post Graduate Industry Experience Winner 2018</p>
                <p className='sub-desc'>Monash University, Melbourne</p>

                <p className='sub-desc'>Feb 2010 - Dec 2014:</p>
                <p className='sub-desc'>Bachelor of Industrial Design</p>
                <p className='sub-desc'>University of New South Wales, Sydney</p>
              </div>
            </div>
            <div className='emp-container'>
              <div>
                <p className='sub-title'>Employment History</p>
                <p className='sub-desc'>Jan 2015 - Dec 2017:</p>
                <p className='sub-desc'>Position: Senior Branding Manager and Designer</p>
                <p className='sub-desc'>Dec 2017 - Feb 2018:</p>
                <p className='sub-desc'>Summer Research Scholarship (Monash University)</p>
              </div>
            </div>

          </Col>
          <Col xs={18} md={12} lg={10} style={{borderLeft: '1px solid #47525D'}}>

            <div className='self-intro container' style={{backgroundColor:this.state.color,fontFamily:this.state.sansSerifFont}}>
              {this.letter('HI!')}
            </div>
            <div className='self-intro container' style={{backgroundColor:this.state.color,fontFamily:this.state.sansSerifFont}}>
              {this.letter('I AM')}

            </div>
            <div className='skill-container'>
              <div>
                <p className='sub-title'>Skills Summary</p>
                <p className='sub-desc'>Language:</p>
                <p className='sub-desc'>Java, Python, JavaScript, HTML, CSS, SQL, REACT, D3.js, Swift, C#, C++, Linux Shell.</p>
                <p className='sub-desc'>Design:</p>
                <p className='sub-desc'>Photoshop, Illustrator, After Effect, Sketch, Principle</p>
              </div>
            </div>
            <div className='skill-container'>
              <div>
                <p className='sub-title'>Highlight Projects</p>
                <p className='sub-desc'>React Website Application</p>
                <p className='sub-desc'> Improve the mental health of international students</p>
              </div>
            </div>
          </Col>
        </Row>

      </div>

    </div>
  );
}
}

export default App;
